import React, { useContext, useState } from 'react'
import './ProductDisplay.css'
import { ShopContext } from '../../Context/ShopContext'

const ProductDisplay = (props) => {
    const {product} = props;
    const {addToCart} = useContext(ShopContext)
    const [quantity, setQuantity] = useState(product.quantity>0 ? 1 : 0)

  return (
    <div className='productdisplay'>
        <div className="productdisplay-left">
            <img className='productdisplay-img' src={product.image} alt="" />
        </div>
        <div className="productdisplay-right">
            <h1>{product.name}</h1>
            <p className="productdisplay-right-price">${product.price}</p>
            <div className="productdisplay-right-cartitems-quantity">
              <button onClick={()=>{quantity>1 ? setQuantity(quantity-1) : setQuantity(quantity)}} class="productdisplay-right-cartitems-quantity-plusminus">{"-"}</button>
              <p class="productdisplay-right-cartitems-quantity-number">{quantity}</p>
              <button onClick={()=>{quantity<3 && quantity<product.quantity ? setQuantity(quantity+1) : setQuantity(quantity)}} class="productdisplay-right-cartitems-quantity-plusminus" aria-label="Increase">{"+"}</button>
            </div>
            {/* <button onClick={()=>{addToCart(product.id)}}>ADD TO CART</button> */}
            {product.quantity>0 ?
            <button className='productdisplay-right-addtocart' onClick={()=>{addToCart(product.id,quantity)}}>ADD TO CART</button> : 
            <button className='productdisplay-right-addtocart' disabled>SOLD OUT</button>}
            {/* <p className="productdisplay-right-category"><span>Category:</span>Women, T-Shirt, Crop Top</p>
            <p className="productdisplay-right-category"><span>Tags:</span>Modern, Latest</p> */}
        </div>
    </div>
  )
}

export default ProductDisplay
