import React from 'react'


// Import Swiper React components
import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

import './PosterSlider.css'
import poster from '../Assets/poster'

// import required modules
import { Autoplay, Pagination, Navigation } from 'swiper/modules';
import Slide from '../Slide/Slide';

const PosterSlider = () => {
  return (
    <div className='posterslider'>
      <Swiper
        spaceBetween={0}
        centeredSlides={true}
        autoplay={{
          delay: 3000,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={false}
        modules={[Autoplay, Pagination, Navigation]}
        className="posterslider-poster"
      >
      {poster.map((item,i)=>{
          return <SwiperSlide className="poster">
              {/* <Link to={item.url} target="_blank" className='poster-link'>
                <img src={item.image} alt="" />
              </Link> */}
              <Slide url={item.url} image={item.image} isNewPage={false}/>
            </SwiperSlide>
      })}
      </Swiper>
    </div>
  )
}

export default PosterSlider
