import React, {useState} from 'react'

import product from '../Components/Assets/product'
import Item from '../Components/Item/Item'
import './CSS/ShopCategory.css'
import productype from '../Components/Assets/productype'

const ShopCategory = () => {

  const [category, setCategory] = useState("all");

  return (
    <div className='shop-category'>
      <div className="shopcategory-products">
        <div className="shopcategory">
          <ul>
            <li>All</li>
            {productype.map((item,i)=>{
              <li>{item.name}</li>
            })}
          </ul>
        </div>
        {product.map((item, i)=>{
          if (item.category===item.category){
            return <Item key={i} id={item.id} name={item.name} image={item.image} price={item.price} category={item.category}/>
          }
        })}
      </div>
    </div>
  )

}
export default ShopCategory
