import React, { useState, useRef } from 'react'
import './Navbar.css'
import logo from '../Assets/logo.png'
import cart_icon from '../Assets/cart_icon.png'
import { Link } from 'react-router-dom'

const Navbar = () => {

    const [menu, setMenu] = useState("home");
    // const menuRef = useRef();
    // const dropdown_toggle = (e) => {
    //     menuRef.current.classList.toggle('nav-menu-visible');
    //     e.target.classList.toggle('open')
    // }

  return (
    <div className='navbar'>
        <div onClick={()=>{setMenu("home");window.scrollTo(0,0)}} className="nav-logo">
            <Link style={{textDecoration: 'none', display: 'flex', alignItems: 'center', gap: '10px'}} to='/'>
                <img src={logo} alt="" />
                <p>EMOKA</p>
            </Link>
        </div>
        <div className="nav-tribar-menu">
            <div className="nav-tribar">
                <input type="checkbox" id="tribar" className="nav-checkbox"/>
                <label for="tribar">â‰¡</label>
            </div>
            <ul className="nav-menu">
                <li onClick={()=>{setMenu("home");window.scrollTo(0,0)}} ><Link style={{textDecoration: 'none', color: menu === "home" ? 'black' : 'inherit' }} to='/'>Home</Link></li>
                <li onClick={()=>{setMenu("shop");window.scrollTo(0,0)}}><Link style={{textDecoration: 'none', color: menu === "shop" ? 'black' : 'inherit'}} to='/shop'>Shop</Link></li>
                <li onClick={()=>{setMenu("contact");window.scrollTo(0,0)}}><Link style={{textDecoration: 'none', color: menu === "contact" ? 'black' : 'inherit'}} to='/contact'>Contact</Link></li>
            </ul>
        </div>

        <div className="nav-cart">
            <Link to='/cart'><img onClick={()=>{setMenu("cart")}} src={cart_icon} alt="" /></Link>
            <div className="nav-cart-count">0</div>
        </div>
    </div>

  )
}

export default Navbar
